mapper.update:
update comment
set user_id = #{userId,jdbcType=INTEGER},
  goods_id = #{goodsId,jdbcType=BIGINT},
  com_msg = #{comMsg,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, user_id, goods_id, com_msg
from comment
where id = #{id,jdbcType=INTEGER}

   select id, goods_id
from comment
 where goods_id = #{id,jdbcType=INTEGER}
