mapper.insert:
insert into comment (id, user_id, goods_id
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{goodsId,jdbcType=BIGINT}
  )

mapper.update:
update comment
set user_id = #{userId,jdbcType=INTEGER},
  goods_id = #{goodsId,jdbcType=BIGINT}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, user_id, goods_id
from comment
where id = #{id,jdbcType=INTEGER}
